CREATE TABLE `items` (  `hash` int(10) unsigned NOT NULL AUTO_INCREMENT,  `title` varchar(255) DEFAULT '',  `about` text default '',  `about_me` text default '',  `stars` decimal(3,2) unsigned DEFAULT '2.50',  `state` enum('waiting','accepted','rejected') DEFAULT 'waiting',  `create_date` datetime DEFAULT NULL,  PRIMARY KEY (`hash`)) ENGINE=myisam  DEFAULT CHARSET=utf8;
CREATE TABLE `images` (  `hash` int(10) unsigned NOT NULL AUTO_INCREMENT,  `item_hash` int(10) unsigned DEFAULT NULL,  `path` varchar(400) DEFAULT NULL,  PRIMARY KEY (`hash`),  KEY `item_hash` (`item_hash`)) ENGINE=myisam  DEFAULT CHARSET=utf8;
